#Le handle le plus court de la liste
def shortest_handle(my_array)
  short = my_array.min{|a,b|a.length <=> b.length}
  puts "Le handle le plus court est : #{short}"
end

#compter le nombre de caracter
def five_crt_count(my_array)
  five_crt = 0
  my_array.each do |handle|
  long = handle.length

    if long == 6
      five_crt += 1
    end
  end
  puts "--> R : Il y a #{five_crt} handle contenant 5 caracteres, @exclut"
  return five_crt
end

#question 8

def five_crt_count1(my_array)
  five_crt = 0
  34.times do |i|
    my_array.each do |handle|
    long = handle.length

    if long == i
      five_crt += 1
    end
    end
    puts "--> R : Il y a #{five_crt} handle contenant #{i} caracteres, @exclut" 
  end
  
  return five_crt
end
