#creation de hash
my_hash=Hash.new(0)
my_array.each do |handle|
  my_hash[handle] += 1

end

#restocker le nombre de lettre comme valeur dans le hash
my_hash.each do |handle,count|
  my_hash[handle]= handle.length
end

#trier le nombre de lettre par ordre croissant
sorted_hash_by_handle = my_hash.sort_by{|handle,count| count}

#trier le nombre de lettre par ordre croissant
sorted_hash_by_key = my_hash.sort_by{|handle,count| handle}

#recupérer le 1er élément
